import React, { Component } from 'react';
import tachyons from 'tachyons';
import './Card.css';

class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Buses: [],
      bookedbus: -1
    };
  }

  async componentDidMount() {
    if (this.props.sbus) {
      try {
        const busData = await Promise.all(
          this.props.routeids.map(id =>
            fetch(`http://localhost:3001/fetchBuses/${id}`)
              .then(res => res.json())
              .then(data => JSON.parse(data))
          )
        );
        this.setState({ Buses: busData.flat() });
      } catch (err) {
        console.error("Error fetching buses:", err);
      }
    }
  }

  handleBooking = (event) => {
    const bookedIndex = parseInt(event.target.name, 10);
    this.setState({ bookedbus: bookedIndex }, () => {
      this.props.SetBus(this.state.Buses[bookedIndex]);
      this.props.onRouteChange('test');
    });
  };

  handleLocation = (event) => {
    event.preventDefault();
    const location = "10.8978,76.9038"; // Replace with dynamic location if available
    window.open(`https://www.google.com/maps?q=${location}`, '_blank');
  };

  LoadBuses = () => {
    return this.state.Buses.map((bus, index) => (
      <div key={index} className="bg-light-yellow br3 pa3 ma2" style={{ width: "550px", margin: "0 auto", marginBottom: "20px" }}>
        <div className="clearfix row-one">
          <div className="column-two p-right-10 w-30 fl">
            <div className="travels lh-24 f-bold d-color">{bus.AgencyName}</div>
            <div className="bus-type f-15 m-top-16 l-color">{bus.AC ? "AC" : "Non-AC"}</div>
            <img src={`https://robohash.org/${index}?200x200`} alt='bus' style={{ width: "50px", height: "50px" }} />
          </div>
          <div className="column-three p-right-10 w-10 fl">
            <div className="dp-time f-15 d-color f-bold">{bus.StartTime}</div>
          </div>
          <div className="column-four p-right-10 w-20 fl">
            <div className="dur l-color lh-12">{bus.TravelTime} hrs.</div>
            <div className="bus-type f-15 m-top-16 d-color">Travel Time</div>
          </div>
          <div className="column-five p-right-10 w-10 fl">
            <div className="bp-time f-15 d-color disp-Inline">05:00</div>
          </div>
          <div className="bg-gold column-seven p-right-10 w-15 fl">
            <div className="seat-fare">
              <div className="fare d-block">INR<br />
                <span className="f-19 f-bold">{bus.Fare}</span>
              </div>
            </div>
          </div>
          <div className="column-eight w-15 fl">
            <div className="seat-left m-top-16">{bus.TotalSeats - bus.ReservedSeats}
              <span className="l-color"> Seats available</span>
            </div>
            <input name={index} className="bg-light-blue dib br3 pa3 ma2" type='submit' onClick={this.handleBooking} value='BOOK' />
          </div>
        </div>
      </div>
    ));
  };

  render() {
    return (
      <div>
        {this.props.sbus ? this.LoadBuses() : (
          <div className="bg-light-yellow dib br3 pa3 ma2 grow">
            <img alt='bus' src={`https://robohash.org/${this.props.id}?200x200`} style={{ width: '80px', height: 'auto' }} />
            <div>
              <h4>BUS</h4>
              <p>FROM: Place1</p>
              <p>TO: Place2</p>
              <p>Driver Name: --</p>
              <p>Driver Phone: --</p>
            </div>
            <input type='button' value='Search Location' style={{ marginLeft: '10px' }} onClick={this.handleLocation} className="btn btn-primary" />
          </div>
        )}
      </div>
    );
  }
}

export default Card;
